package pe.edu.galaxy.training.java.jd0.matematicos;

public class AppMath {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.PI);
		System.out.println(Math.E);
		
		System.out.println(Math.pow(2, 3)); // Pontencia
		
		System.out.println(Math.sqrt(16));	// Raiz cuadrada
		
		System.out.println(Math.abs(5));
		System.out.println(Math.abs(-5));
		
		System.out.println(Math.max(8, 5));
		System.out.println(Math.min(8, 5));
		
		System.out.println(Math.round(3.75));

	}

}
